<template>
  <div class="bg">
    <svg ref="svgRef" version="1.1" xmlns="http://www.w3.org/2000/svg" @click="boardClickHandle">
      <defs>
        <radialGradient id="shadow-black" r="1.0">
          <stop offset="0" stop-color="black" stop-opacity="1.0"></stop>
          <stop offset="30%" stop-color="black" stop-opacity="1.0"></stop>
          <stop offset="31%" stop-color="#333333" stop-opacity="0.6"></stop>
          <stop offset="34%" stop-color="#333333" stop-opacity="0.50"></stop>
          <stop offset="40%" stop-color="#333333" stop-opacity="0.5"></stop>
          <stop offset="50%" stop-color="#333333" stop-opacity="0.0"></stop>
        </radialGradient>
        <radialGradient id="shadow-white" r="1.0">
          <stop offset="0" stop-color="white" stop-opacity="1.0"></stop>
          <stop offset="30%" stop-color="white" stop-opacity="1.0"></stop>
          <stop offset="31%" stop-color="#333333" stop-opacity="0.6"></stop>
          <stop offset="34%" stop-color="#333333" stop-opacity="0.50"></stop>
          <stop offset="40%" stop-color="#333333" stop-opacity="0.5"></stop>
          <stop offset="50%" stop-color="#333333" stop-opacity="0.0"></stop>
        </radialGradient>
        <image id="white-shell" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-1-20-87" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-2-20-88" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-3-20-89" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-4-20-90" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-5-20-91" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-6-20-92" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-7-20-93" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-8-20-94" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="white-shell-9-20-95" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
        <image id="black-shell" width="40" height="40" x="0" y="0"
               xlink:href="data:image/png;base64,"></image>
      </defs>

      <g class="coordinate-labels">
        <text v-for="item in lineNumbers" :x="item.x" :y="item.y" :font-size="`${item.fontSize}px`" font-weight="500"
              fill="#444444">{{ item.text }}
        </text>
      </g>
      <g class="shadow-layer">
        <circle
            v-for="(item, index) in points"
            :fill="`url(#shadow-${index % 2 === 0 ? 'black' : 'white'})`"
            :cx="boardSetting.point.shadowSize"
            :cy="boardSetting.point.shadowSize"
            :r="boardSetting.point.shadowRadius *  0.95"
            :transform="`translate(${item.x * boardSetting.ss + boardSetting.ox - boardSetting.mid},${item.y * boardSetting.ss + boardSetting.oy - boardSetting.mid})`"
        />
      </g>
      <g class="grid">
        <g v-for="(item, index) in points" :transform="`translate(${item.x * boardSetting.ss + boardSetting.ox - boardSetting.mid},${item.y * boardSetting.ss + boardSetting.oy - boardSetting.mid})`">
          <use :href="`#${index % 2 === 0 ? 'black' : 'white'}-shell`" :x="boardSetting.point.offset" :y="boardSetting.point.offset"></use>
        </g>
      </g>
    </svg>
  </div>
</template>
<script setup>
import { ref, nextTick, useTemplateRef, reactive } from 'vue'
import { computeBoard } from '@/components/go/goban'

const { onBoardClick, points } = defineProps({
  onBoardClick: {
    type: Function,
  },
  points: {
    default: [],
    type: Array,
  }
})

const svg = useTemplateRef('svgRef')
const lineNumbers = ref([])

const boardSetting = reactive({
  ss: 0,
  ox: 0,
  oy: 0,
  mid: 0,
  squareSize: 0,
  w: 0,
  h: 0,
  showLabel: false,
  point: {
    shadowSize: 0,
    shadowRadius: 0,
    offset: 0
  }
})

const render = () => {
  const setting = computeBoard(svg, 19, 19, 39, true)
  Object.assign(boardSetting, setting)
}

const boardClickHandle = event => {
  const x = parseInt((event.offsetX - boardSetting.ox + boardSetting.mid) /  boardSetting.ss)
  const y = parseInt((event.offsetY - boardSetting.oy + boardSetting.mid) /  boardSetting.ss)
  onBoardClick(x, y)
}

render()
</script>

